    font-size: 1.5rem;
}

div{
    text-align: center;

}

button{
    color: blue !important;
    background-color: darkgoldenrod;
    cursor: pointer;
    padding: 1rem 2rem;
}

input{
    color: blue !important;
    background-color: white;
}

form{
    color: red !important;
}

nav {
    background-color: darkgoldenrod !important;
    font-size: 3rem;
    font-weight: 400;
    line-height: 1.5;
    -webkit-text-size-adjust: 100%;
