    position: relative;
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    justify-content: space-between;
    padding-top: .5rem;
    padding-bottom: .5rem;
}

body {
    margin: 0;
    background-color: black;
}

@import url('https://fonts.googleapis.com/css2?family=Lora:wght@400;500;700&display=swap');
* {
    margin: 0px;
    padding: 0px;
    box-sizing: border-box;
}

:root {
    --color-white: #fff;
    --color-pink: #d12f7a;
    --color-black: #000;
    --color-blue:#1407ca;
    --color-purple:#5106a7;
    --color-red:#e71b1b;
    --color-gray:#2f2f3b;
}

html{
    scroll-behavior: smooth;
    font-size: 10px;
}

body{
    font-family: 'Lora', serif;
}

#header{
    display: flex;
    justify-content: space-between;
    align-items: center;

    width: 100%;

    padding: 15px 30px;
    position: fixed;

    background: transparent;
    z-index: 2;
    transition: all .4s ease-out;
}

.header-title{
    text-decoration: none;
}

.header-title h1{
    color: var(--color-white);
    font-size: 2rem;
    transition: opacity .3s ease;
}

.header-title h1:hover{
    opacity: .8;
}

.navbar{
    max-width: 350px;
    width: 100%;
}

.navbar ul {
    display: flex;
    align-items: center;
    justify-content: space-around;
}

.navbar ul li{
    list-style: none;
}

.navbar ul li a{
    text-decoration: none;
    color: var(--color-white);
    font-size: 1.4rem;
    font-weight: 700;
}

#person{
    height: 100vh;
    width: 100%;

    display: flex;
    align-items: center;

}

.person-content{
    height: 100%;
    width: 50%;

    display: flex;
    justify-content: center;
    align-items: center;

    flex-direction: column;

    background: var(--color-black);
}

.person-content div h1{
    color: transparent;
    padding: 0px 4px;
    position: relative;

    font-size: 4.5rem;
    line-height: 6rem;

    animation: written_text .5s ease forwards;
}

.person-content div h1:nth-child(1){
    animation-delay: 1s;
}

.person-content div h1:nth-child(2){
    animation-delay: 2s;
}

.person-content div h1:nth-child(3){
    animation: written_text_name .5s ease forwards;
    animation-delay: 3s;
}

.person-content div h1 span{
    position: absolute;
    top: 0;
    left: 0;


    width: 0px; 
    height: 100%;
    background: var(--color-blue);
    animation: written_text_box 1s ease;
    animation-delay: .5s;
}

.person-content div h1:nth-child(1) span{
    animation-delay: .5s;
}

.person-content div h1:nth-child(2) span{
    animation-delay: 1.5s;
}

.person-content div h1:nth-child(3) span{
    animation-delay: 2.5s;
}

.person-name{
    margin-top: 11rem;
}

.stacks-content{
    display: flex;
    /* align-items: center; */
    justify-content: center;

    flex-direction: column;
    margin-top: 6rem;
}

.stacks-content h3{
    color: var(--color-white);
    font-size: 1.15rem;
    margin-left: 20px;
}

.stacks{
    display: flex;
    align-items: center;
    flex-direction: row;

}

.stacks ul{
    width: 90px;
    height: 90px;
}

.stacks ul li{
    display: flex;
    align-items: center;

    position: relative;

    margin: 10px;
    padding: 10px;

    list-style: none;
}

.stacks ul li div{
    width: 50px;
    height: 60px;
    /* margin: 0px 0px 2px 2px; */
}

.stacks ul li div:hover::after{
    content: attr(data);
    color: var(--color-purple);
    text-align: center;
    font-size: 1.5rem;
    font-weight: bold;
    position: absolute;
    top: 65px;
    /* margin: 10px; */
    left: 12.5px;

    width: 45px;
    height: 20%;   
}

.stacks ul li div img{
    width: 45px;
    height: auto;
    align-items: center;
    justify-content: center;

    transition: transform .3s ease;

}

.stacks ul li div img:hover{
    transform: scale(1.2);
    cursor: pointer;
}


.person-background{
    height: 100vh;
    width: 50%;
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;

    flex-direction: column;
    background-image: url(../IMG/florian.jpg);
    background-size: cover;
    background-position: top center;
    z-index: 1;
} 

.person-background::after{
    content: "";
    position: absolute;
    top: 0;
    left: 0;

    height: 100%;
    width: 100%;

    opacity: .98;
    z-index: -1;

    background:
        linear-gradient(270deg, transparent 30%, var(--color-black)),
        linear-gradient(180deg, transparent 10%, var(--color-black))
        ;
}

.wrapper-profile-photo{
    display: flex;
    justify-content: center;
    align-items: center;

    width: 300px;
    padding: 14px 14px;
    margin: 20px 15px;

    background: 
        radial-gradient(rgba(0,0,0,0.15) 60%, transparent 0),
        radial-gradient(#000 68%, transparent 0),
        linear-gradient(to top right, #ffa500 0%, #ff1493 100%);
    border-radius: 100%;

}

.profile-photo{
    border-radius: 50%;
    width: 100%;

}

.links a img{
    width: 30px;
    height: 30px;

    margin: 10px;
    filter: brightness(.8);
    transition: .3s ease;
}

.links a img:hover{
    filter: brightness(1);
    transform: scale(1.15);
}

/* primeira parte finalizada */

#about{
    height: 100vh;
    width: 100%;
    background: var(--color-black);

    display: flex;
    color: var(--color-white);
}

.background-about{
    width: 50%;
    height: 100%;

    display: flex;
    justify-content: center;
    align-items: center;
}

.img-wrapper{
    height: 100%;
    width: 100%;

    position: relative;

}

.img-wrapper::after{
    content: "";
    position: absolute;
    top: 0;
    left: 0;

    height: 100%;
    width: 100%;

    /* opacity: .9; */

    background:
        linear-gradient(180deg, transparent 30%, var(--color-black)), 
        linear-gradient(90deg, transparent 30%, var(--color-black)),
        linear-gradient(0deg, transparent 65%, var(--color-black))
    ;
}

.img-wrapper img{
    height: 100%;
    width: 100%;

    object-fit: cover;
}

.box-about{
    position: relative;
    display: flex;
    justify-content: center;
    flex-direction: column;

    width: 50%;
    height: 100%;

    padding: 3rem;
}

.section-subtitle h2 p{
    display: inline-block;
    font-size: 3rem;
}

.section-subtitle h2 p:nth-child(2) span{
    padding: 3px 5px;
    border-radius: 4px;

    color: var(--color-black);
    background: var(--color-white);
}

.section-subtitle h2 p:nth-child(3) span{
    padding: 3px 5px;
    border-radius: 4px;

    color: var(--color-black);
    background: var(--color-red);
}

.box-about h3{
    margin: 3rem;
    font-size: 2rem;
}

.box-about h3 p{
    display: inline-block;

    width: fit-content;
    padding: 2px 4px;
    border-radius: 4px;

    color: var(--color-black);
    background: var(--color-pink);
}

.box-about h4{
    margin: 1rem 3rem;
    font-size: 1.5rem;
}

.box-about h4 p {
    display: inline;
    padding: 2px 4px;

    color: var(--color-blue);
    font-size: bold;
    font-weight: 1.75rem;
}

.button-cta {
    display: flex;

    max-width: 190px;
    margin: 4rem 3rem;
}

.button-cta a{
    display: inline-block;

    width: 100%;
    max-width: 172px;

    padding: 10px;
    margin-bottom: 3rem;

    font-family: 'Lora', serif;
    font-weight: 1000;
    letter-spacing: .15rem;
    text-align: center;
    font-size: 1.3rem;

    text-decoration: none;
    border-radius: 10px;
    cursor: pointer;

    color: var(--color-black);
    background: var(--color-white);

}

.button-cta a:hover{
    transition: all .3s ease;
    filter: brightness(.8);

}

/* segunda parte finalizada */

#projects{
    height: 100vh;
    width: 100%;
    background: var(--color-black);

    display: flex;
    flex-direction: column;


}

.projects-title h2{
    margin: 3rem;
    padding: 1rem;

    font-size: 3rem;
    color: var(--color-white);

}

/* .wrapper-projects{
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: row;

    height: 500px;

    margin-top: 2rem;

} */

.container-projects{
    height: 100%;
    width: 100%;

    display: flex;
    justify-content: center;
    align-items: center;
}

.swiper{
    height: 100%;
    width: 100%;

    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: row;

    height: 500px;

    margin-top: 2rem;
}

.card-project{
    width: 320px;
    height: 450px;

    padding: 30px 20px 20px;
    /* margin: 0px 10px; */

    position: relative;
    background: var(--color-white);

    display: flex;
    align-items: center;
    flex-direction: column;

    border-radius: 20px;
    z-index: 1; 
}

.card-project::after{
    content: "";
    position: absolute;
    top: 0;
    left: 0;

    height: 40%;
    width: 100%;

    background: var(--color-purple);
    border-radius: 20px 20px 0px 0px;
    z-index: -1;
}

/* .content-project{
    width: 100%;
    height: 100%;


    display: flex;
    flex-direction: column;

    z-index: 1;
} */

.image-project{
    max-height: 240px;
    max-width: 250px;
    height: 100%;
    width: 100%;

    overflow: hidden;
    position: relative;
    border-radius: 30px;


    border: 4px solid var(--color-purple);
    margin: 0 auto;
}

.image-project img{
    height: 100%;
    width: 100%;
    border-radius: 30px;

    object-fit: cover;
    position: absolute;
    top: 0;
    left: 0;

    border: 2px solid var(--color-white);
    transition: transform .3s ease-out;
    cursor: pointer;

}

.image-project img:hover{
    transform: scale(1.1);

}

.card-project span{
    color: var(--color-black);
    font-weight: bold;
    font-size: 1.7rem;

    margin: .75rem auto .5rem auto;
}

.card-project p{
    color: var(--color-gray);
    font-weight: bold;
    font-size: 1.1rem;
    margin: 0rem .75rem;
}

.cta-project{
    width: 200px;
    margin: auto auto 15px auto;

    display: flex;
    align-items: center;
    justify-content: space-between;
}

.cta-project a{
    padding: 5px 16px;

    font-size: 1.25rem;
    color: var(--color-white);
    background: var(--color-purple);

    border-radius: 10px;

    text-decoration: none;

    transition: opacity .3s ease;
}

.cta-project a:hover {
    opacity: .7;
}

/* Projects Finalizado */

#contacts{
    display: flex;
    align-items: center;
    flex-direction: column;
    height: 40vh;
    width: 100%;

    background: var(--color-black);

    color: var(--color-white);
}

.contact-icon-content h2{
    margin: 3rem 3rem 3rem 0rem;
    padding: 1rem 1rem 1rem 0rem;

    font-size: 3rem;


}

.contact-icons-wrapper{
    display: flex;
    align-items: center;
    flex-direction: row;
}

.contact-icon-content{
    display: flex;
    flex-direction: row;
    align-items: center;

    margin: 0rem 3rem;
    padding: 1rem;
}

.contact-icon{
    height: 40px;
    width: 40px;

    display: flex;
    justify-content: center;
    align-items: center;

    margin-right: 2rem;

    background: var(--color-white);
    padding: 1rem;
    border-radius: 50%;
}

.contact-icon i{
    font-size: 2rem;
    color: var(--color-blue);
}

.contact-info span{
    font-size: 1.5rem;
    font-weight: bold;
}

.contact-info p{
    font-size: 1.2rem;
    margin: .25rem 0rem;
}

.contact-info a{
    display: block;
    font-size: 1.2rem;
    margin: .25rem 0rem;

    text-decoration: none;
    color: var(--color-white);
}

.contact-info a:hover{
    transition: color .3s ease;
    color: var(--color-blue);
}

#footer{
    height: 15vh;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;

    margin-top: auto;

    background-color: var(--color-gray);
}

.container-footer{
    display: flex;
    justify-content: center;
    align-items: center;

    flex-direction: column;

    color: var(--color-white);
}

@keyframes written_text {
    100%{
        color: var(--color-white);
    }
}

@keyframes written_text_name {
    100%{
        color: var(--color-purple);
    }
}

@keyframes written_text_box {
    50%{
        width: 100%;
        left: 0;
    }
    100%{
        width: 0;
        left: 100%;
    }
}

@media screen and (max-width: 850px){
    html{
        font-size: 9px;
    }

    .header-title h1{
        font-size: 1.55rem;
    }

    #person{
        min-height: 200vh;
        flex-direction: column;
    }

    .person-content{
        width: 100%;
        height: 50%;
    }

    .stacks-content h3{
        font-size: 1.5rem;
    }

    .stacks ul li img{
        width: 40px;
        padding: 1px 1px 5px 5px;
    }

    .person-background{
        width: 100%;
        /* height: 50%; */
    }

    .person-background::after{
        content: "";
        position: absolute;
        top: 0;
        left: 0;

        height: 100%;
        width: 100%;

        opacity: .98;
        z-index: -1;

        background:
            linear-gradient(180deg, transparent 8%, var(--color-black)),
            linear-gradient(0deg, transparent 8%, var(--color-black));
    }

    .wrapper-profile-photo{
        max-width: 100%;
        min-width: 200px;
    }

    .background-about{
        width: 0;
        height: 0;
    }

    .box-about{
        width: 100%;
        background-image: url(../IMG/img.2.jpg);
        background-size: cover;
        background-position: top center;
        z-index: 1;
    }

    .box-about::after{
        content: "";
        position: absolute;
        top: 0;
        left: 0;

        height: 100%;
        width: 100%;

        opacity: .98;
        z-index: -1;

        background:
            linear-gradient(180deg, transparent 8%, var(--color-black)),
            linear-gradient(0deg, transparent 8%, var(--color-black));
    }
    .box-about h4{
        background: #000001b4;;
    }

    .stacks ul{
        width: 70px;
        height: 70px;
    }

    #contacts{
        height: 50vh;
    }

    .card-project{
        width: 285px;
        height: 400px;
    }

    .contact-icons-wrapper{
        align-items: baseline;
        flex-direction: column;
    }

    .contact-info span{
        font-size: 1.8rem;
        font-weight: bold;
    }

    .contact-info p{
        font-size: 1.5rem;
        margin: .25rem 0rem;
    }

    .card-project p{
        color: var(--color-black);
        font-weight: bold;
        font-size: 1.25rem;
    }

    #footer{
        height: 11vh;
    }
}

