.image-project{
    max-height: 240px;
    max-width: 250px;
    height: 100%;
    width: 100%;

    overflow: hidden;
    position: relative;
    border-radius: 30px;


    border: 4px solid var(--color-purple);
    margin: 0 auto;
}

.image-project img{
    height: 100%;
    width: 100%;
    border-radius: 30px;

    object-fit: cover;
    position: absolute;
    top: 0;
    left: 0;

    border: 2px solid var(--color-white);
    transition: transform .3s ease-out;
    cursor: pointer;

}

.image-project img:hover{
    transform: scale(1.1);

}

